import { useState, useEffect } from 'react';
import { ReactDOM } from 'react-dom';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { CardContainer } from '../../containers/Card/Card';
import { ModalSavePin } from '../../containers/ModalSavePin/ModalSavePin';
import { ModalCreateFolder } from '../../containers/ModalCreateFolder/ModalCreateFolder';
import { Notification } from '../../components/Notification/Notification';
import { useAppContext } from '../../store/AppContext';
import { saveFolderSuccessType } from "../../store/types";

const pinsData = [
    {
        id: '123',
        title: 'Trigonometria',
        image: 'https://picsum.photos/200/300?53',
        total: 0,
    },
    {
        id: '133',
        title: 'JavaScript',
        image: 'https://picsum.photos/200/300?13',
        total: 0,
    },
    {
        id: '134',
        title: 'React JS',
        image: 'https://picsum.photos/200/300?23',
        total: 0,
    },
];

export const HomePage = () => {
    const { state, dispatch } = useAppContext();
    const [showFeedback, setShowFeedback] = useState(false);

    useEffect(() => {
        if (state.type === saveFolderSuccessType) {
            setShowFeedback(true);
        }
    }, [state.type])

    return (
        <div>
            <ModalSavePin open={state.mode === 'savePin'} />
            <ModalCreateFolder open={state.mode === 'createFolder'} />
            {showFeedback && (
                <Notification
                    message='Criado com sucesso!'
                    onClose={() => setShowFeedback(false)}
                />
            )}
            <Container fluid>
                <Row>
                    {pinsData.map(pin => (
                        <Col key={pin.id} xs={12} md={2}>
                            <CardContainer {...pin} />
                        </Col>
                    ))}
                </Row>
            </Container>
        </div>
    )
}
