import * as types from './types';
import * as pinService from '../services/pinService';

export const openModalSavePinAction = () => ({
    type: types.openModalSavePinType
});

export const closeModalAction = () => ({
    type: types.closeModalsType
});

export const fetchFoldersTypInitAction = () => ({
    type: types.fetchFoldersIniType
});

export const fetchFolderSuccess = () => ({
    type: types.fetchFoldersType
});

export const fetchFoldersType = async () => {
    const folders = await pinService.getFolders();
    return {
        type: types.fetchFoldersType
    }
};
