import { useEffect } from "react";
import ListGroup from "react-bootstrap/ListGroup";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { Modal } from "../../components/modal/Modal";
import { Button } from "../../components/Button/Button";
import { useAppContext } from "../../store/AppContext";
import { closeModalAction } from "../../store/actions";
import { fetchFoldersAction } from "../../store/actions";

export const ModalSavePin = ({ open }) => {
    const {state, dispatch} = useAppContext();
    const handleClose = () => {
        console.log('Fechando')
        dispatch(closeModalAction())
    }

    useEffect(() => {
        fetchFoldersAction(dispatch);
    }, [])

    return (
        <Modal
            title="Salvar Pin"
            open={open}
            onHide={handleClose}
            controls={[
                {
                    label: 'Criar Pasta',
                    variant: 'secondary',
                    loading: false,
                    loadingLabel: "Criando",
                    onclick: () => {
                        console.log('Clicou em criar pasta')
                    }
                }
            ]}>
            <ListGroup variant="flush">
                {state}
                <ListGroup.Item>
                    <Row>
                        <Col xs={8}>Matemática</Col>
                        <Col xs={4} className="text-end"><Button label="Salvar" loadingLabel="Salvando" /></Col>
                    </Row>
                </ListGroup.Item>
                <ListGroup.Item>
                    <Row>
                        <Col xs={8}>Matemática</Col>
                        <Col xs={4} className="text-end"><Button label="Salvar" loadingLabel="Salvando" /></Col>
                    </Row>
                </ListGroup.Item>
            </ListGroup>
        </Modal>
    )
}
